mapper.insert:
INSERT INTO BOARD 
(

) 
VALUES 
(
	  #{num}
	, #{title}
	, #{content}
	, #{writer}
	, 0
	, IFNULL(#{noticeYn}, 'N')
	, 'N'
	, IFNULL(#{secretYn}, 'N')
	, NOW()
	, NULL
	, NULL
)

mapper.update:
UPDATE BOARD
SET UPDATE_TIME = NOW()

	, WRITER = #{writer}


	, TITLE = #{title}


	, CONTENT = #{content}

, NOTICE_YN = IFNULL(#{noticeYn}, 'N')
, SECRET_YN = IFNULL(#{secretYn}, 'N')
WHERE NUM = #{num}

UPDATE BOARD 
SET VISIT_CNT = VISIT_CNT+1
WHERE NUM = #{num}

UPDATE BOARD
SET DELETE_TIME = NOW()
, DELETE_YN = 'Y'

	, WRITER = #{writer}

WHERE NUM = #{num}
